// store.ts
import { configureStore } from '@reduxjs/toolkit';
import { useDispatch } from 'react-redux';
import { ThunkAction } from 'redux-thunk';
import rootReducer, { RootState } from './reducers';

const store = configureStore({
  reducer: rootReducer,
});

export type AppDispatch = typeof store.dispatch;
export const useAppDispatch = () => useDispatch<AppDispatch>();

// Define the async thunk type
export type AppThunk<ReturnType = void> = ThunkAction<ReturnType, RootState, unknown, any>;

// Add the fetchData async thunk
export const fetchData = (): AppThunk => async (dispatch) => {
  try {
    // Your API call here (e.g., using axios)
    const response = await axios.get('https://api.example.com/data');
    dispatch(fetchDataSuccess(response.data));
  } catch (error) {
    dispatch(fetchDataFailure(error.message ?? 'An error occurred.'));
  }
};

export default store;
